glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Constant {
  float ringWidth;
  float startInnerRadius;
  float endInnerRadius;
  float animationDuration;
  float staticRingWidth;
  float staticRingOpacity;
};
float roundedRectSDF(vec2 uv, vec2 center, float size, float cornerRadius) {
  vec2 p = abs(uv - center) * 2.0;
  p = p - size + cornerRadius;
  float d = length(max(p, 0.0)) - cornerRadius;
  return min(max(p.x, p.y), 0.0) + d;
}
void main () {
  float time = cc_time.x;
  float normalizedTime = mod(time, animationDuration) / animationDuration;
  float currentInnerRadius = mix(startInnerRadius, endInnerRadius, normalizedTime);
  float outerRadius = currentInnerRadius + ringWidth;
  vec2 center = vec2(0.5, 0.5);
  float cornerRadius = ringWidth * 0.5;
  float staticCornerRadius = staticRingWidth * 0.5;
  float outerDist = roundedRectSDF(v_uv0, center, outerRadius, cornerRadius);
  float innerDist = roundedRectSDF(v_uv0, center, currentInnerRadius, cornerRadius);
  float edgeSmoothness = 0.05;
  float outerEdge = smoothstep(edgeSmoothness, -edgeSmoothness, outerDist);
  float innerEdge = smoothstep(-edgeSmoothness, edgeSmoothness, innerDist);
  float animatedRing = outerEdge * innerEdge;
  float staticInnerRadius = startInnerRadius - staticRingWidth * 0.5;
  float staticOuterRadius = startInnerRadius + staticRingWidth * 0.5;
  float staticOuterDist = roundedRectSDF(v_uv0, center, staticOuterRadius, staticCornerRadius);
  float staticInnerDist = roundedRectSDF(v_uv0, center, staticInnerRadius, staticCornerRadius);
  edgeSmoothness = 0.03;
  float staticOuterEdge = smoothstep(edgeSmoothness, -edgeSmoothness, staticOuterDist);
  float staticInnerEdge = smoothstep(-edgeSmoothness, edgeSmoothness, staticInnerDist);
  float staticRing = staticOuterEdge * staticInnerEdge * staticRingOpacity;
  float combinedRing = animatedRing * (1.0 - normalizedTime) + staticRing;
  combinedRing = min(combinedRing, 1.0);
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  o.rgb = v_color.rgb;
  o.a = combinedRing * v_color.a;
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

