glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
  in vec2 a_uv0;
  out vec2 v_uv0;
#endif
void main() {
  mat4 mvp;
  #if CC_USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Properties {
  vec4 u_color;
  float u_time;
  float u_speed;
  float u_width;
  float u_rotate;
  vec4 u_lrbt;
};
float linear(float x0, float x1, float y0, float y1, float inputX) {
  return (y1 - y0) * (inputX - x0) / (x1 - x0) + y0;
}
void main() {
  vec4 o = vec4(1.0, 1.0, 1.0, 1.0);
  vec4 alpha = vec4(0.0);
  #if USE_TEXTURE
    o = texture2D(texture, v_uv0);
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  if (o.w >= 1.0) {
    vec2 uv = vec2(
      linear(u_lrbt[0], u_lrbt[1], 0.0, 1.0, v_uv0.x),
      linear(u_lrbt[2], u_lrbt[3], 0.0, 1.0, v_uv0.y)
    );
    float yrodio = mod(u_rotate, 180.0) / 90.0;
    float xrodio = 1.0 - abs(yrodio);
    if (u_rotate < 0.0) {
      yrodio = -yrodio;
      xrodio = -xrodio;
    }
    float scanPos = fract(u_time * u_speed - (uv.x * xrodio  + uv.y * yrodio) * 0.2);
    float halfWidth = u_width * 0.5;
    float scanLine = smoothstep(0.5 - halfWidth, 0.5, scanPos) -
                 smoothstep(0.5, 0.5 + halfWidth, scanPos);
    if(scanLine < 0.0 || scanLine > 1.0){
      gl_FragColor = alpha;
    }
    else{
      gl_FragColor = vec4(u_color.rgb, scanLine);
    }
  }
  else {
    gl_FragColor = alpha;
  }
}

