glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
  in vec2 a_uv0;
  out vec2 v_uv0;
#endif
void main() {
  mat4 mvp;
  #if CC_USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D u_fluxayTexture;
uniform ARGS {
  float u_time;
  float useGrayscale;
};
void main() {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o = texture2D(texture, v_uv0);
  #endif
    float gray = dot(o.rgb, vec3(0.2126, 0.7152, 0.0722));
    o = vec4(vec3(gray)*  useGrayscale + (1.0 - useGrayscale)*o.rgb, o.a);
  o *= v_color;
  ALPHA_TEST(o);
  if (o.a >= 1.0) {
    vec2 fluxayUV = vec2(v_uv0.x, v_uv0.y);
    fluxayUV.x -= u_time - 1.0;
    vec4 fluxay = texture2D(u_fluxayTexture, fluxayUV);
    gl_FragColor = o + fluxay;
  } else {
    gl_FragColor = o;
  }
}

