glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
out vec2 v_modelPos;
#if USE_TEXTURE
    in vec2 a_uv0;
    out vec2 v_uv0;
#endif
void main () {
    vec4 pos = vec4(a_position, 1);
    float time = cc_time.x;
    vec4 wpos  = cc_matWorld * pos;
    float y = a_color.r * 10000.0 - 5000.0;
    float h = a_color.a * 500.0;
    v_modelPos = vec2(wpos.x, (wpos.y - y)/h);
    #if CC_USE_MODEL
        pos = cc_matViewProj * cc_matWorld * pos;
    #else
        pos = cc_matViewProj * pos;
    #endif
    #if USE_TEXTURE
        v_uv0 = a_uv0;
    #endif
    v_color = a_color;
    gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
    in vec2 v_uv0;
    in vec2 v_modelPos;
    uniform sampler2D texture;
    uniform suibianqu {
        float _time;
    };
#endif
void main () {
    vec4 o = vec4(1, 1, 1, 1);
    vec2 uv = vec2(v_uv0);
    float time = cc_time.x;
    float y = v_modelPos.y;
    float wind_x = 0.2 + 0.2 * cos(time * 1.1 + v_modelPos.y);
    float offset_x = 0.05 * pow(y, 4.0) * wind_x;
    float new_x = uv.x + offset_x;
    float offset_y = pow(offset_x, 2.0) * 3.0;
    float new_y = uv.y - offset_y;
    uv.x = fract(new_x);
    uv.y = fract(new_y);
    #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, uv);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, uv + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
    #endif
    ALPHA_TEST(o);
    #if USE_BGRA
        gl_FragColor = o.bgra;
    #else
        gl_FragColor = o.rgba;
    #endif
}

