  vec4 noiseTile;
  vec4 textureTile;
};
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}
float gridRandom(vec2 worldPos, float gridSize) {
    vec2 gridPos = floor(worldPos / gridSize);
    return random(gridPos);
}
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec2 uv = vec2(v_uv0);
  float gridSize = 256.0 / textureTile.x;
  float pureColor = step(0.34, gridRandom(v_worldPos.xy + vec2( gridSize * 0.5, 0.0) , gridSize));
