glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D nosiseTex;
#endif
uniform suibianqu{
  float edge;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 nosie = vec4(1, 1, 1, 1);
  float time = cc_time.x;
  #if USE_TEXTURE
  #endif
  vec2 uv = vec2(v_uv0.x, v_uv0.y + 0.0042);
  float flowSpeed = time *  0.0005;
  vec2 noise_uv = vec2(uv.x - flowSpeed * 2.0, uv.y - flowSpeed) * 100.0;
  nosie = texture2D(nosiseTex, noise_uv);
  vec2 flow_uv =vec2(uv.x - flowSpeed * 2.0, uv.y - flowSpeed) * 100.0;
  vec2 center_uv = uv - vec2(0.5, 0.5);
  float len = abs(center_uv.x) + abs(center_uv.y);
  float edge0 = edge + nosie.b * 0.002 - 0.001;
  float edge1 = edge + 0.004 + nosie.g * 0.002 - 0.001;
  float alpha = smoothstep(edge0, edge1, len);
  o = texture2D(texture, flow_uv);
  o.a = alpha * v_color.a;
  vec3 baseColor = o.rgb * v_color.rgb;
  float luminance = dot(baseColor, vec3(0.299, 0.587, 0.114));
  float dynamicBoost = 0.5 * (1.0 - luminance);
  float fixedBoost = 0.3;
  o.rgb = baseColor + vec3(fixedBoost + dynamicBoost);
  vec4 originalColor = o;
  vec4 finalColor = originalColor;
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = finalColor.bgra;
  #else
    gl_FragColor = finalColor.rgba;
  #endif
}

