glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
out vec3 v_worldPos;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  vec4 wpos  = cc_matWorld * pos;
  v_worldPos = vec3(wpos.xy, 0.0);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec2 v_uv0;
in vec3 v_worldPos;
uniform sampler2D texture;
uniform sampler2D noiseTex;
uniform suibianqu{
  vec4 noiseTile;
  vec4 textureTile;
  vec4 mixColA;
  vec4 mixColB;
  vec2 moveScale;
  vec2 lightPos;
  float timeScale;
  float strength;
  float _time;
  float mixValue;
  float inCircle;
  float outCircle;
  float circleRate;
  float limitValue;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec2 uv = vec2(v_uv0);
      float time = cc_time.x;
      #if USE_CUSTOM_TIME
        time = _time;
      #endif
      #if USE_NOISE
        uv = uv +  texture(noiseTex, uv * noiseTile.xy + time * 0.1 * timeScale * noiseTile.zw).xy * 0.1 * strength;
        float x = uv.x;
        uv = uv * textureTile.xy + textureTile.zw;
        uv.y -= (sin(x * 0.2 * moveScale.x) * 0.5)  +  time * moveScale.y * 0.1;
      #else
        uv = uv * textureTile.xy + textureTile.zw;
      #endif
      o = texture(texture, uv);
      float y = lightPos.y + (v_worldPos.y - lightPos.y) * circleRate;
      float dis = mix(0.01, 1.0, smoothstep(inCircle, outCircle, distance(vec2(v_worldPos.x, y), lightPos.xy)));
      float lerpT = dis * mixValue * limitValue;
      vec3 lerpColor = o.rgb * mix(mixColA.rgb, mixColB.rgb, mixValue);
      o.rgb = mix(o.rgb, lerpColor, lerpT);
      o *= v_color;
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

