glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D texture1;
uniform sampler2D texture2;
uniform sampler2D texture3;
uniform sampler2D texture4;
uniform sampler2D texture5;
#endif
const float INDEX_SCALE = 255.0;
const float TEXTURE_THRESHOLD_1 = 42.5;
const float TEXTURE_THRESHOLD_2 = 85.0;
const float TEXTURE_THRESHOLD_3 = 127.5;
const float TEXTURE_THRESHOLD_4 = 170.0;
const float TEXTURE_THRESHOLD_5 = 212.5;
void main () {
  vec4 o;
  #if USE_TEXTURE
    float index = v_color.r * INDEX_SCALE;
    if (index < TEXTURE_THRESHOLD_1) {
      o = texture(texture, v_uv0);
    } else if (index < TEXTURE_THRESHOLD_2) {
      o = texture(texture1, v_uv0);
    } else if (index < TEXTURE_THRESHOLD_3) {
      o = texture(texture2, v_uv0);
    }else if (index < TEXTURE_THRESHOLD_4) {
      o = texture(texture3, v_uv0);
    }else if (index < TEXTURE_THRESHOLD_5) {
      o = texture(texture4, v_uv0);
    } else {
      o = texture(texture5, v_uv0);
    }
  #endif
  o.a *= v_color.a;
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

