glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
#if USE_TINT
  in vec4 a_color0;
#endif
in vec2 a_uv0;
out vec2 v_uv0;
out vec2 v_worldPos;
out vec4 v_color;
#if USE_TINT
  out vec4 v_dark;
#endif
void main () {
  mat4 mvp;
  vec4 pos = vec4(a_position, 1);
  vec4 wpos  = cc_matWorld * pos;
  v_worldPos = wpos.xy;
  #if CC_USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  #if USE_TINT
    v_dark = a_color0;
  #endif
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
in vec2 v_uv0;
in vec2 v_worldPos;
in vec4 v_color;
#if USE_TINT
  in vec4 v_dark;
#endif
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
  uniform Properties {
    vec4 mixColA;
    vec4 mixColB;
    vec2 lightPos;
    float GOHST;
    float light;
    float mixValue;
    float inCircle;
    float outCircle;
    float circleRate;
    float limitValue;
  };
void main () {
  vec4 finalColor = vec4(1.0);
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    finalColor.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    finalColor.a *= texture_tmp.a;
  #else
    finalColor *= texture_tmp;
  #endif
  finalColor.a *= v_color.a;
  bool isWhiteLight = abs(v_color.r - 1.0) < 0.01 &&
                     abs(v_color.g - 1.0) < 0.01 &&
                     abs(v_color.b - 1.0) < 0.01;
  if(!isWhiteLight) {
    float maxChannel = max(max(finalColor.r, finalColor.g), finalColor.b);
    float minChannel = min(min(finalColor.r, finalColor.g), finalColor.b);
    float colorDelta = maxChannel - minChannel;
    if(finalColor.b > finalColor.r * 1.2 &&
       finalColor.b > finalColor.g * 1.2 &&
       colorDelta > 0.1) {
      float intensity = finalColor.b;
      finalColor.rgb = v_color.rgb  * (intensity / max(max(v_color.r, v_color.g), v_color.b)) * 1.2;
    }
  }
float mixFactor = step(0.1, GOHST);
float gray = dot(finalColor.rgb, vec3(0.299, 0.587, 0.114));
vec3 blueColor = mix(vec3(0.678, 0.847, 0.902), vec3(0.5, 0.55, 0.9), gray);
vec3 mixedColor = mix(finalColor.rgb, vec3(0.99, 0.462, 0.062), light);
finalColor.rgb = mix(mixedColor, blueColor, mixFactor);
finalColor.a *= mix(1.0, 0.8, mixFactor);
  float y = lightPos.y + (v_worldPos.y - lightPos.y) * circleRate;
  float dis = mix(0.0, 1.0, smoothstep(inCircle + 300.0, outCircle + 300.0, distance(vec2(v_worldPos.x, y), lightPos.xy)));
  float lerpT = dis * mixValue * limitValue;
  vec3 lerpColor = finalColor.rgb * mix(mixColA.rgb, mixColB.rgb, mixValue);
  finalColor.rgb = mix(finalColor.rgb, lerpColor, lerpT);
  ALPHA_TEST(finalColor);
  gl_FragColor = finalColor;
}

