glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
  in vec2 a_uv0;
  out vec2 v_uv0;
#endif
void main() {
  vec4 pos = vec4(a_position, 1);
  vec4 wpos = cc_matWorld * pos;
  #if CC_USE_MODEL
    pos = cc_matViewProj * cc_matWorld * pos;
  #else
    pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D texture1;
uniform sampler2D texture2;
uniform sampler2D texture3;
uniform sampler2D texture4;
uniform sampler2D texture5;
uniform sampler2D texture6;
uniform sampler2D texture7;
uniform sampler2D texture8;
uniform sampler2D texture9;
uniform sampler2D texture10;
uniform sampler2D texture11;
uniform sampler2D texture12;
uniform sampler2D texture13;
uniform sampler2D texture14;
uniform sampler2D texture15;
const float INDEX_SCALE = 255.0;
const float TEXTURE_THRESHOLD_0 = 15.9375;
const float TEXTURE_THRESHOLD_1 = 31.8750;
const float TEXTURE_THRESHOLD_2 = 47.8125;
const float TEXTURE_THRESHOLD_3 = 63.7500;
const float TEXTURE_THRESHOLD_4 = 79.6875;
const float TEXTURE_THRESHOLD_5 = 95.6250;
const float TEXTURE_THRESHOLD_6 = 111.5625;
const float TEXTURE_THRESHOLD_7 = 127.5000;
const float TEXTURE_THRESHOLD_8 = 143.4375;
const float TEXTURE_THRESHOLD_9 = 159.3750;
const float TEXTURE_THRESHOLD_10 = 175.3125;
const float TEXTURE_THRESHOLD_11 = 191.2500;
const float TEXTURE_THRESHOLD_12 = 207.1875;
const float TEXTURE_THRESHOLD_13 = 223.1250;
const float TEXTURE_THRESHOLD_14 = 239.0625;
const vec3 ORANGE_COLOR = vec3(0.99, 0.462, 0.062);
void main() {
  float index = v_color.r * INDEX_SCALE;
  vec4 o;
  if (index < TEXTURE_THRESHOLD_0) {
    o = texture(texture, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_1) {
    o = texture(texture1, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_2) {
    o = texture(texture2, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_3) {
    o = texture(texture3, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_4) {
    o = texture(texture4, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_5) {
    o = texture(texture5, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_6) {
    o = texture(texture6, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_7) {
    o = texture(texture7, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_8) {
    o = texture(texture8, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_9) {
    o = texture(texture9, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_10) {
    o = texture(texture10, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_11) {
    o = texture(texture11, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_12) {
    o = texture(texture12, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_13) {
    o = texture(texture13, v_uv0);
  } else if (index < TEXTURE_THRESHOLD_14) {
    o = texture(texture14, v_uv0);
  } else {
    o = texture(texture15, v_uv0);
  }
  if(v_color.g > 0.5){
    vec3 tintYellowColor = vec3(1.0, 1.0, 0.0);
    vec3 channelDiff = vec3(
      o.b - o.r * 1.2,
      o.b - o.g * 1.2,
      max(max(o.r, o.g), o.b) - min(min(o.r, o.g), o.b)
    );
    if (all(greaterThan(channelDiff.xy, vec2(0.0))) && channelDiff.z > 0.05) {
      o.rgb = tintYellowColor * o.b * 1.2;
    }
  }
  o.rgb = mix(o.rgb, ORANGE_COLOR, v_color.b);
  o.a *= v_color.a;
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

